mapper.update:

update ClassTable
set c_name = #{cName,jdbcType=VARCHAR},
  c_headmaster_name = #{cHeadmasterName,jdbcType=VARCHAR},
  c_phone = #{cPhone,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR}
where c_id = #{cId,jdbcType=INTEGER}

mapper.select:

select c_id, c_name, c_headmaster_name, c_phone, status
from ClassTable
where c_id = #{cId,jdbcType=INTEGER}
