mapper.update:

update ExchangeTable
set e_name = #{eName,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR},
  e_sort = #{eSort,jdbcType=INTEGER},
  e_remarks = #{eRemarks,jdbcType=LONGVARCHAR}
where e_id = #{eId,jdbcType=INTEGER}

mapper.select:

select e_id, e_name, status, e_sort, e_remarks
from ExchangeTable
where e_id = #{eId,jdbcType=INTEGER}
