mapper.update:

update TypeofMeeting
set t_name = #{tName,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR},
  t_remarks = #{tRemarks,jdbcType=VARCHAR},
  t_sort = #{tSort,jdbcType=INTEGER}
where t_id = #{tId,jdbcType=INTEGER}

mapper.select:

select t_id, t_name, status, t_remarks, t_sort
from TypeofMeeting
where t_id = #{tId,jdbcType=INTEGER}
